  "bp.redDotsDesc": { "en-GB": "Red dots = detected heartbeats. Each wave corresponds to one cardiac cycle.", "pt-BR": "Pontos vermelhos = batimentos detectados. Cada onda corresponde a um ciclo cardíaco." },
  "bp.rhythmLabel": { "en-GB": "Rhythm", "pt-BR": "Ritmo" },
