  "bp.wrongCameraDesc": { "en-GB": "You appear to be using the front camera. For accurate PPG readings, use the rear camera with the flash LED. Place your finger on the back of the phone.", "pt-BR": "Você parece estar usando a câmera frontal. Para leituras PPG precisas, use a câmera traseira com o flash LED. Coloque o dedo na parte de trás do telefone." },
  "bp.fingerGood": { "en-GB": "✅ Finger position: Good — hold steady", "pt-BR": "✅ Posição do dedo: Boa — mantenha firme" },
  "bp.fingerFair": { "en-GB": "⚠️ Finger position: Fair — press more firmly over camera + flash", "pt-BR": "⚠️ Posição do dedo: Regular — pressione mais firmemente sobre a câmera + flash" },
  "bp.fingerPoor": { "en-GB": "⚠️ Finger position: Poor — cover both the camera lens and flash LED", "pt-BR": "⚠️ Posição do dedo: Ruim — cubra a lente da câmera e o flash LED" },
  "bp.fingerNone": { "en-GB": "❌ No finger detected — place your fingertip firmly over the rear camera and flash", "pt-BR": "❌ Nenhum dedo detectado — coloque a ponta do dedo sobre a câmera traseira e o flash" },
