  "home.phone": { "en-GB": "Phone", "pt-BR": "Telefone" },
  "bp.reminderLabel": { "en-GB": "Weekly reminder", "pt-BR": "Lembrete semanal" },
  "bp.reminderOn": { "en-GB": "Weekly BP reminder enabled", "pt-BR": "Lembrete semanal de PA ativado" },
  "bp.reminderOff": { "en-GB": "Weekly BP reminder disabled", "pt-BR": "Lembrete semanal de PA desativado" },
