// Default locale
let currentLocale: Locale = "en-GB";

export function setLocale(locale: Locale) {
  currentLocale = locale;
  if (typeof window !== "undefined") {
    localStorage.setItem("clinic-locale", locale);
  }
}

export function getLocale(): Locale {
  if (typeof window !== "undefined") {
    const saved = localStorage.getItem("clinic-locale") as Locale | null;
    if (saved && (saved === "en-GB" || saved === "pt-BR")) {
      currentLocale = saved;
    }
  }
  return currentLocale;
}

export function t(key: string, locale?: Locale): string {
  const l = locale || currentLocale;
  return translations[key]?.[l] || translations[key]?.["en-GB"] || key;
}

export { translations };
export type { Locale };
